$('h1').on('mouseover', function(){
	$('h1').css('color', 'red');
$('h1').on('mouseleave', function(){
	$('h1').css('color', '#ffe900');
	});
});
$('li a:eq(0)').on('mouseover', function(){
	$('li a:eq(0)').css('color', 'White');
$('li a:eq(0)').on('mouseleave', function(){
	$('li a:eq(0)').css('color', 'black');
	});
});
